-- 12. Determine la cantidad de prestamos de cada libro.-- Voy a agruparlos por isbn y cuento los isbn, si quiero el nombre JOINEO  con librosSELECT isbn, COUNT(isbn) AS cantidadPrestamosFROM PrestamosGROUP BY isbn-- Parte 2: Joineo LibrosSELECT *FROM (SELECT isbn, COUNT(isbn) AS cantidadPrestamos	  FROM Prestamos	  GROUP BY isbn) AS agrupadaPrestamosINNER JOIN Libros ON agrupadaPrestamos.isbn = Libros.isbn-- Parte 3: Me quedo con nombre y cantPrestamosSELECT Libros.nombre, agrupadaPrestamos.cantidadPrestamosFROM (SELECT isbn, COUNT(isbn) AS cantidadPrestamos	  FROM Prestamos	  GROUP BY isbn) AS agrupadaPrestamosINNER JOIN Libros ON agrupadaPrestamos.isbn = Libros.isbn