-- Muestre los nombres de los socios que piden libros de autores alemanes. ----Paso 1: Join entre Prestamos, AutorLibro, AutoresSELECT *FROM PrestamosINNER JOIN AutorLibro ON Prestamos.isbn = AutorLibro.isbnINNER JOIN Autores ON AutorLibro.codigo = Autores.codAut-- Paso 2: Filtrar por autores alemanesSELECT *FROM PrestamosINNER JOIN AutorLibro ON Prestamos.isbn = AutorLibro.isbnINNER JOIN Autores ON AutorLibro.codigo = Autores.codAutWHERE Autores.pais = 'Alemania'-- Paso 3: JOINEAMOS con SociosSELECT *FROM PrestamosINNER JOIN AutorLibro ON Prestamos.isbn = AutorLibro.isbnINNER JOIN Autores ON AutorLibro.codigo = Autores.codAutINNER JOIN Socios ON Socios.codSocio = Prestamos.socioWHERE Autores.pais = 'Alemania'-- PASO 4: Solo nos quedamos con el nomnbre de los socio SELECT DISTINCT Socios.codSocio, Socios.nombreFROM PrestamosINNER JOIN AutorLibro ON Prestamos.isbn = AutorLibro.isbnINNER JOIN Autores ON AutorLibro.codigo = Autores.codAutINNER JOIN Socios ON Socios.codSocio = Prestamos.socioWHERE Autores.pais = 'Alemania'